mapper.select:
select

  distinct


from sys_menu




  order by ${orderByClause}



    limit ${offset}, ${limit}


    limit ${limit}

select 

from sys_menu
where meun_id = #{meunId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from sys_menu
where meun_id = #{meunId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

mapper.insert:
insert into sys_menu (meun_id, id, brief, 
  meun_name, parent_id, remark
  )
values (#{meunId,jdbcType=VARCHAR}, #{id,jdbcType=VARCHAR}, #{brief,jdbcType=VARCHAR}, 
  #{meunName,jdbcType=VARCHAR}, #{parentId,jdbcType=VARCHAR}, #{remark,jdbcType=VARCHAR}
  )

mapper.update:
update sys_menu
set brief = #{brief,jdbcType=VARCHAR},
  meun_name = #{meunName,jdbcType=VARCHAR},
  parent_id = #{parentId,jdbcType=VARCHAR},
  remark = #{remark,jdbcType=VARCHAR}
where meun_id = #{meunId,jdbcType=VARCHAR}
  and id = #{id,jdbcType=VARCHAR}

